mapper.select:
SELECT A1.MKR_CD , A1.MKR_NM , A1.DMPRD_YN , A1.RLS_YN , A2.ULD_FILE_NM , A2.FILE_EXTN_NM FROM TN_CAR_MAKER A1 LEFT JOIN CM_ATCH_FILE A2 ON A1.ATCH_FILE_CD = A2.ATCH_FILE_CD
#{item}
#{item}
ORDER BY A1.REG_DT DESC, A1.MKR_NM

mapper.insert:

    SELECT IFNULL(#{MKR_CD}, FN_GET_KEYCODE())

INSERT INTO TN_CAR_MAKER (
       MKR_CD
       , MKR_NM
       , MKR_ENG_NM
       , DMPRD_YN
       , RLS_YN
       , USE_YN
       , ATCH_FILE_CD
       , REG_USER_CD
       , REG_USER_IP
       , REG_DT
       , MDFCN_USER_CD
       , MDFCN_USER_IP
       , MDFCN_DT
)
VALUES (
       #{MKR_CD}
       , #{MKR_NM}
       , #{MKR_ENG_NM}
       , #{DMPRD_YN}
       , #{RLS_YN}
       , #{USE_YN}
       , #{ATCH_FILE_CD}
       , #{SESSION_USER_CD}
       , #{SESSION_USER_IP}
       , NOW()
       , #{SESSION_USER_CD}
       , #{SESSION_USER_IP}
       , NOW()
)
ON DUPLICATE KEY UPDATE
   MKR_NM = #{MKR_NM}
   , MKR_ENG_NM = #{MKR_ENG_NM}
   , DMPRD_YN = #{DMPRD_YN}
   , RLS_YN = #{RLS_YN}
   , USE_YN = #{USE_YN}
   , ATCH_FILE_CD = #{ATCH_FILE_CD}
   , MDFCN_USER_CD = #{SESSION_USER_CD}
   , MDFCN_USER_IP = #{SESSION_USER_IP}
   , MDFCN_DT = NOW()
