mapper.select:
SELECT A1.TRSM_CD , A1.TRSM_NM , A1.TRSM_ENG_NM , A1.TRSM_ABBR , A1.RLS_YN , A1.USE_YN , A1.SORT_SEQ FROM TN_CAR_TRSM A1
#{item}
#{item}
ORDER BY A1.SORT_SEQ, A1.TRSM_NM

mapper.insert:

    SELECT IFNULL(#{TRSM_CD}, FN_GET_KEYCODE())

INSERT INTO TN_CAR_TRSM (
       TRSM_CD
       , TRSM_NM
       , TRSM_ENG_NM
       , TRSM_ABBR
       , APLCN_BGNG_YR
       , APLCN_END_YR
       , RLS_YN
       , USE_YN
       , SORT_SEQ
       , REG_USER_CD
       , REG_USER_IP
       , REG_DT
       , MDFCN_USER_CD
       , MDFCN_USER_IP
       , MDFCN_DT
)
VALUES (
       #{TRSM_CD}
       , #{TRSM_NM}
       , #{TRSM_ENG_NM}
       , #{TRSM_ABBR}
       , #{APLCN_BGNG_YR}
       , #{APLCN_END_YR}
       , #{RLS_YN}
       , #{USE_YN}
       , #{SORT_SEQ}
       , #{SESSION_USER_CD}
       , #{SESSION_USER_IP}
       , NOW()
       , #{SESSION_USER_CD}
       , #{SESSION_USER_IP}
       , NOW()
)
ON DUPLICATE KEY UPDATE
   TRSM_NM = #{TRSM_NM}
   , TRSM_ENG_NM = #{TRSM_ENG_NM}
   , TRSM_ABBR = #{TRSM_ABBR}
   , APLCN_BGNG_YR = #{APLCN_BGNG_YR}
   , APLCN_END_YR = #{APLCN_END_YR}
   , RLS_YN = #{RLS_YN}
   , USE_YN = #{USE_YN}
   , SORT_SEQ = #{SORT_SEQ}
   , MDFCN_USER_CD = #{SESSION_USER_CD}
   , MDFCN_USER_IP = #{SESSION_USER_IP}
